import React from "react";
import Layout from "../../Containers/Layout";
import styles from './profile.module.scss';
import popupstyle from './popup.css'
import 'antd/dist/antd.css';
import {UserOutlined, MailOutlined, LockOutlined, PhoneOutlined, ScheduleOutlined, getTwoToneColor} from '@ant-design/icons';
import { Tabs, Card, Button, Row, Col} from 'antd';
import {Link} from 'react-router-dom';
import MainContext from "../../Context/provider/context";
import {useContext, useEffect} from 'react';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import PopUp from "./PopUp";


const { TabPane } = Tabs;

var check = false;

const content = (details, members, modalShow, togglePop) => {
    return (
        <div className={styles.titlu}> Profilul meu:
            <Tabs>
                <TabPane className={styles.continut0} tab="Contul meu" key="1" active>
                <p>Nume: {details.firstname}</p>
                <p>Prenume: {details.lastname} </p>
                <p>Email: {details.email}</p>
                <p>Nume utilizator: {details.username}</p>
                <p>Nume organizatie: {details.organizationName} </p>
                </TabPane>
                <TabPane className={styles.continut0} tab="Membrii organizatie" key="2" active>
                    <List component="nav" className={styles.continut0} aria-label="contacts">
                    {
                        members && members.map(function(item, i) {
                            return(
                            <ListItem button key={i} onClick={togglePop}>
                                <li>
                                {i}{'. '}{item.firstname}{' '}{item.lastname}
                                </li>
                            </ListItem>)
                        })
                    }
                    </List>
                </TabPane>
                <TabPane className={styles.continut} tab="Resurse" key="3" active>
                    <button 
                        variant="contained"
                        className={styles.button2}
                        size={'large'}><Link className={styles.button2.p} to={'/resursa'}> Adauga resursa</Link></button>
                    <button 
                        variant="contained"
                        className={styles.button2}
                        size={'large'}><Link className={styles.button2.p} to={'/resursa'}> Sterge resursa</Link></button>
                    <Row gutter={16}>
                        <Row span={16}>
                            <Card title={<span className={styles.continut}>Resursa 1  
                                <button
                                    variant="contained"
                                    className={styles.button}
                                    size={'large'}
                                    // onClick={() => handleRezervare()}
                                >
                                    <p>REZERVA</p>
                                </button>
                            </span>} className={styles.continut2} bordered={true} activeTabKey>
                            <p>Status:</p>
                            <p>Timp:</p>
                            </Card>
                        </Row>
                        <Row span={16}>
                            <Card title={<span className={styles.continut}>Resursa 2  
                                <button
                                    variant="contained"
                                    className={styles.button}
                                    size={'large'}
                                    // onClick={() => handleRezervare()}
                                >
                                    <p>REZERVA</p>
                                </button>
                            </span>} className={styles.continut2} bordered={true} activeTabKey>
                            <p>Status:</p>
                            <p>Timp:</p>
                            </Card>
                        </Row>
                        <Row span={16}>
                            <Card title={<span className={styles.continut}>Resursa 3  
                                <button
                                    variant="contained"
                                    className={styles.button}
                                    size={'large'}
                                    // onClick={() => handleRezervare()}
                                >
                                    <p>REZERVA</p>
                                </button>
                            </span>} className={styles.continut2} bordered={true} activeTabKey>
                            <p>Status:</p>
                            <p>Timp:</p>
                            </Card>
                        </Row>
                        <Row span={1}>
                            <Card title={<span className={styles.continut}>Resursa 4 
                                <button
                                    variant="contained"
                                    className={styles.button}
                                    size={'large'}
                                    // onClick={() => handleRezervare()}
                                >
                                    <p>REZERVA</p>
                                </button>
                            </span>} className={styles.continut2} bordered={true} activeTabKey>
                            <p>Status:</p>
                            <p>Timp:</p>
                            </Card>
                        </Row>
                        <Row span={1}>
                            <Card title={<span className={styles.continut}>Resursa 5 
                                <button
                                    variant="contained"
                                    className={styles.button}
                                    size={'large'}
                                    // onClick={() => handleRezervare()}
                                >
                                    <p>REZERVA</p>
                                </button>
                            </span>} className={styles.continut2} bordered={true} activeTabKey>
                            <p>Status:</p>
                            <p>Timp:</p>
                            </Card>
                        </Row>
                    </Row>

                </TabPane>
            </Tabs>
        </div>
    )
};


const Profile = (props) => {
    const { details } = useContext(MainContext).profile;
    const { getMembers, members } = useContext(MainContext).profile;
    const [modalShow, setModalShow] = React.useState(false);

    const togglePop = () => {
        if (modalShow === false) {
            setModalShow(true)
        } else {
            setModalShow(false)
        }
    }

    if (check === false) {
        getMembers(details.organizationId).then(() => {
            check = true;
        })
    }
    return (
        <div>
            <div>
                {modalShow ? <PopUp className={popupstyle} toggle={togglePop} /> : null}
            </div>
            <Layout content={content(details, members, modalShow, togglePop)}/>
        </div>
    )
};



export default Profile;
